                                    }
                                }
                                else if (timeRangeValue == 0 || timeRangeValue2 == 0)
                                {
                                    if (timeTable[23, week] == "")
                                    {
                                        check = true;
                                    }
                                    else if (timeTable[23, week] != "")
                                    {
                                        check = false;
                                        break;
                                    }
                                }
                                isTimeOut1 = false;
                                break;
                            }

                        }
                    }
                }
            }

        }
    }
